codon = []
aminoAcid = []
content = []
def readFile():
    i = 0
    file = open('/Users/saltside/Desktop/rosalind/codonTable.rtf','rU')
    for line in file:
        line = line.rstrip('\n')
        for word in line.split():
            content.append(word)
            
    return content
def process():
    readFile()
    for x in range(13,len(content)):
        if content[x] != '}':
            if x%2 == 0:
                if '\\' in content[x]:
                    aminoAcid.append(content[x][0])
                else:
                    aminoAcid.append(content[x])
            else:
                codon.append(content[x])
    return codon, aminoAcid

def transcribe(dnaSeq):
    polypeptide = ''
    for x in range(0,len(dnaSeq),3):
        codon1 = dnaSeq[x:x+3]
       
        for j in range(0,len(codon)):
            if codon1 == codon[j] and aminoAcid[j] != 'Stop':
                polypeptide += aminoAcid[j]
    return polypeptide

process()
print(transcribe(""))
                
        
   
